// BuyStocks.jsx
import { Link, useNavigate } from "react-router-dom";

import React, { useState } from 'react';
import './BuyStocks.scss'; // Import SCSS file for styling

const BuyStocks = () => {
//   const [symbol, setSymbol] = useState('');
//   const [quantity, setQuantity] = useState(0);
  const [err,setErr] = useState(false);
  const navigate  = useNavigate()
  const [inputs,setInputs] = useState({
    stock_id:"",
    quantity:"",
  })
  const handleChange = e=>{
    setInputs(prev=>({...prev,[e.target.name]:e.target.value}))
  }
  const handleBuy = async (e) => {
    e.preventDefault()
      try{
        await login(inputs)
        navigate("/")
      }catch(err){
        setErr(err.response.data)
      }
  };
//   const handleBuy = () => {
//     // Handle buy logic here, e.g., send a request to the backend
//   };

  return (
    <div className="buy-stocks-container">
      <h2>Buy Stocks</h2>
      <input type="email" placeholder="email" name="email" onChange={handleChange} />
      <input type="stock_id" name = "stock_id" onChange={handleChange} placeholder="stock_id" />
      <input type="quantity" name = "quantity" onChange={handleChange} placeholder="quantity" />
      {err&&err}
      <button onClick={handleBuy}>Buy</button>
    </div>
  );
};

export default BuyStocks;
