// SellStocks.jsx
import React, { useState } from 'react';
import axios from "axios";
import './SellStocks.scss'; // Import SCSS file for styling

const SellStocks = () => {
  const [symbol, setSymbol] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [err,setErr] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'symbol') {
      setSymbol(value);
    } else if (name === 'quantity') {
      setQuantity(value);
    }
  };

  const handleSell = async () => {
    try {
      const res = await axios.post("http://localhost:8080/server/order/sell", {
        stock_id: symbol,
        quantity: quantity
      }, {
        withCredentials: true,
      });
      setErr("Transaction Successful");
      // setErr(res.data);
    } catch(err) {
      setErr(err.response.data);
    }
  };

  return (
    <div className="sell-stocks-container">
      <h2>Sell Stocks</h2>
      <input 
        type="text" 
        name="symbol"
        value={symbol} 
        onChange={handleChange} 
        placeholder="Stock Symbol" 
        style={{ marginBottom: '10px' }} 
      />
      <br />
      <input 
        type="number" 
        name="quantity"
        value={quantity} 
        onChange={handleChange} 
        placeholder="Quantity" 
        style={{ marginBottom: '10px' }} 
      />
      <br />
      {err && <p>{err}</p>}
      <button onClick={handleSell} style={{ marginBottom: '10px' }}>Sell</button>
    </div>
  );
};

export default SellStocks;
